mapper.insert:
	insert
	into companys
		(
		  comp_no
		, comp_name
		, comp_info
		, comp_email
		, comp_address
		, comp_logo
		, comp_headcount
		, comp_birth
		, created_at
		, updated_at
		, user_no
		, comp_pm
		)
		values
		(
		   seq_comp.nextval
         , #{compName}
         , #{compInfo}
         , #{compEmail}
         , #{compAddress}
         , #{compLogo}
         , #{headCount}
         , #{compBirth}
         , default
         , default
         , #{userNo}
         , #{userNo}
        )

   		 insert
   		  into compindus
   			 (
		   		comp_no
		   	  , indus_no
		   	 )
   			 values
   			 (
               #{compNo}
             , #{indusNo}
             )
