mapper.insert:
insert
	into users
	(
	   user_no
	 , login_type
	 , user_email
	 , user_pwd
	 , user_name
	 , user_birth
	 , user_posi
	 , user_status
	 , user_info
	 , user_edu
	 , user_avatar
	 , created_at
	 , updated_at
	 , access_token
	 , certify_ques
	 , certify_ans
	 , email_auth
	)
	values
	(
	   seq_user.nextval
	 , default   
	 , #{email}
	 , #{userPwd}
	 , #{userName}
	 , #{userBirth}
	 , #{userPosi}
	 , #{status}
	 , #{userInfo}
	 , #{userEdu}
	 , #{userAvatar}
	 , sysdate
	 , sysdate
	 , #{accessToken}
	 , #{certifyQues}
	 , #{certifyAns}
	 , default
	)

mapper.update:
update users
    set
     email_auth = #{emailAuth}
    where user_email=#{email}
     and user_name=#{userName}

update users
	set
	 email_auth = 'Y'
	where user_email=#{email}
	 and email_auth=#{emailAuth}
