mapper.insert:
insert
	into member
	(
	   user_no
	 , login_type
	 , user_email
	 , user_pwd
	 , user_name
	 , user_birth
	 , user_posi
	 , user_status
	 , user_info
	 , user_edu
	 , user_avatar
	 , created_at
	 , updated_at
	 , access_token
	 , certify_ques
	 , certify_ans
	)
	values
	(
	 , seq_user.nextval
	 , default   
	 , #{email}
	 , #{userPwd}
	 , #{userName}
	 , #{userBirth}
	 , #{userPosi}
	 , #{status}
	 , #{userInfo}
	 , #{userEdu}
	 , #{userAvatar}
	 , sysdate
	 , sysdate
	 , #{accessToken}
	 , #{certifyQues}
	 , #{certifyAns}
	)
