mapper.insert:

insert
	  into projects
	      (
	       project_id
	     , user_no  
	     , project_name
	     , project_position
	     , project_start
	     , project_end
	      )
	    values
	      (
	      seq_project.nextval 
	    , #{userNo}
	    , #{projectName}
	    , #{projectPosition}
	    , #{projectStart}
	    , #{projectEnd}
	      )
