mapper.insert:

    		insert into 
    		       recruits
    		       (
    		         RECRUIT_NO
					,RECRUIT_TITLE
					,RECRUIT_INFO
					,RECRUIT_STATE
					,RECRUIT_MAIN
					,RECURIT_POSI
					,RECRUIT_IMG
					,RECRUIT_MINSALARY
					,RECRUIT_MAXSALARY
					,RECRUIT_REQU
					,RECRUIT_DL
					,RECRUIT_COUNT
					,COMP_NO
					,CREATED_AT
    		       )
    			   values
    			   (
    			     seq_recruit.nextval
    			    ,#{recruitTitle}
    			    ,#{recruitInfo}
    			    ,default
    			    ,#{recruitMain}
    			    ,#{recruitPosi}
    			    ,#{recruitImg}
    			    ,#{recruitMinSal}
    			    ,#{recruitMaxSal}
    			    ,#{recruitRequ}
    			    ,#{recruitDl}
    			    ,default
    			    ,#{compNo}
    			    ,default

    			   )

mapper.select:

    		select count(*)
			  from bookmarks
			 where recruit_no = #{rno}

select count(distinct comp_no) from recruits left join recruit_skills using(recruit_no) left join skills using(skill_no) left join compindus using(comp_no) left join industries using(indus_no) left join companys using(comp_no) where recruit_state=0
and upper(recruit_title) like upper('%' || #{k} || '%')
recurit_posi = #{p}
upper(indus_name) like upper('%' || #{i} || '%')
upper(skill_name) like upper('%' || #{t} || '%')
recruit_requ = #{c}
and recruit_minsalary>=#{sal}
and #{sal}>=recruit_maxsalary
upper(comp_address) like upper('%' || #{addr} || '%')

select distinct comp_no from recruits left join recruit_skills using(recruit_no) left join skills using(skill_no) left join compindus using(comp_no) left join industries using(indus_no) left join companys using(comp_no) where recruit_state=0
and upper(recruit_title) like upper('%' || #{k} || '%')
recurit_posi = #{p}
upper(indus_name) like upper('%' || #{i} || '%')
upper(skill_name) like upper('%' || #{t} || '%')
recruit_requ = #{c}
and recruit_minsalary>=#{sal}
and #{sal}>=recruit_maxsalary
upper(comp_address) like upper('%' || #{addr} || '%')

select RECRUIT_NO ,RECRUIT_TITLE ,RECRUIT_INFO ,RECRUIT_STATE ,RECRUIT_MAIN ,RECURIT_POSI ,RECRUIT_IMG ,RECRUIT_MINSALARY ,RECRUIT_MAXSALARY ,RECRUIT_REQU ,RECRUIT_DL ,RECRUIT_COUNT ,COMP_NO ,TO_CHAR(CREATED_AT,'YYYY/MM/DD') CREATED_AT from recruits where recruit_state=0
and comp_no=#{cno}
and upper(recruit_title) like upper('%' || #{k} || '%')
recurit_posi = #{p}
upper(indus_name) like upper('%' || #{i} || '%')
upper(skill_name) like upper('%' || #{t} || '%')
recruit_requ = #{c}
and recruit_minsalary>=#{sal}
and #{sal}>=recruit_maxsalary
upper(comp_address) like upper('%' || #{addr} || '%')

mapper.update:
UPDATE NUKACOLA.APPLIES t
SET t.APPLY_PROG = #{skillNo}
WHERE t.APPLY_NO = #{recruitNo}
